#include <cmath>
#include <iostream>

bool sumOfTwoCubes(int n) {
    int lo = 1;
    int hi = static_cast<int>(round(pow(n, 1.0 / 3.0)));
    while (lo <= hi) {
        int curr = (lo * lo * lo + hi * hi * hi);
        if (curr == n) {
            return true;
        }
        if (curr < n) {
            lo += 1;
        } else {
            hi -= 1;
        }
    }
    return false;
}

int main() {
    int N = 28;
    if (sumOfTwoCubes(N)) {
        std::cout << " True " << std::endl;
    } else {
        std::cout << " False " << std::endl;
    }
    return 0;
}
