int maxPresum(const std::vector<int>& a, const std::vector<int>& b) {
    int X = std::max(a[0], 0);
    std::vector<int> a_prefix = a;
    for (size_t i = 1; i < a_prefix.size(); ++i) {
        a_prefix[i] += a_prefix[i - 1];
        X = std::max(X, a_prefix[i]);
    }
    int Y = std::max(b[0], 0);
    std::vector<int> b_prefix = b;
    for (size_t i = 1; i < b_prefix.size(); ++i) {
        b_prefix[i] += b_prefix[i - 1];
        Y = std::max(Y, b_prefix[i]);
    }
    return X + Y;
}

int main() {
    std::vector<int> A = {2, -1, 4, -5};
    std::vector<int> B = {4, -3, 12, 4, -3};
    std::cout << maxPresum(A, B) << std::endl;
